$.fn.extend
  ${1:bobIsMyUncle}: (options) ->
    settings = {}
    settings = $.extend settings, options

    @each ()->
      ${2:$(@).replace '<div style="background: red; padding: 10px; color: #ff0000;">Bob\'s your uncle.</div>'}

